package Lec1;

public class If_elseDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Hi");
		int x = 100;
		int y = 12;
		
		if(x<y)
		{
			System.out.println("x is smaller");
		}
		else
		{
			System.out.println("x is greater");
		}
		
		System.out.println("Bye");

	}

}
